mapper.select:
SELECT BUYLIST_DATE, PRODUCT_NAME, PRODUCT_PRICE, BUY_CNT, BUYLIST_TOTALPRICE, PRODUCT_ID FROM (SELECT BUYLIST_DATE, PRODUCT_NAME, PRODUCT_PRICE, BUY_CNT, BUYLIST_TOTALPRICE, PRODUCT_ID, ROWNUM AS RNUM FROM (SELECT BUYLIST_DATE, PRODUCT_NAME, PRODUCT_PRICE, BUY_CNT, BUYLIST_TOTALPRICE, PRODUCT_ID FROM BUYLISTVIEW
WHERE BUYLIST_DATE >= to_date(#{startDate},'YYYY-MM-DD') AND BUYLIST_DATE <= to_date(#{endDate},'YYYY-MM-DD')
))
WHERE RNUM>=#{startRow} and RNUM<=#{endRow}
